//   {
//     text: 'Get an ISA',
//     category: 'Money'
//   },
//   {
//     text: 'Cook rice',
//     category: 'Food'
//   },
//   {
//     text: 'Tidy house',
//     category: 'Misc'
//   }
// ]

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ],
    filteredTasks: [],
    selectedCategory: ''
  }

  changeCategory = (event) => {
    event.persist()
    event.target.classList.add("selected")
    console.log(event.target)
    this.setState({
      selectedCategory: event.target.name
    })
    if (this.state.selectedCategory !== "All") {
      let filteredTasks = this.state.tasks.filter(task => task.category === event.target.name)
      this.setState({
        filteredTasks: filteredTasks
      })
      console.log(filteredTasks)
    } else {
      this.setState({
        tasks: this.state.tasks
      })
      console.log(this.state.tasks)
    }
  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <CategoryButtons changeCategory={this.changeCategory} categories={CATEGORIES}/>
        <TaskContainer tasks={this.state.tasks}/>
      </div>
    );
  }
}


export default App;
