import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
import TaskContainer from './TaskContainer'
import CategoryButtons from './CategoryButtons'

class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ]
  }

  changeCategory = (event) => {
    event.persist()
    // event.target.classList.add("selected")
    console.log(event.target.name.toLowerCase())
    let filteredTasks = this.state.tasks.filter(task => task.category === event.target.name.toLowerCase())
    console.log(filteredTasks)
    // this.setState({

    // })

  }

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <CategoryButtons changeCategory={this.changeCategory} categories={CATEGORIES}/>
        <TaskContainer tasks={this.state.tasks}/>
      </div>
    );
  }
}


export default App;
